import { useState } from "react";

export default function Matches() {
  const [matches, setMatches] = useState([]);
  const [showPopup, setShowPopup] = useState(false);
  const [form, setForm] = useState({ team1: "", team2: "", date: "", time: "" });

  const addMatch = () => {
    if (form.team1 && form.team2 && form.date && form.time) {
      setMatches([...matches, form]);
      setForm({ team1: "", team2: "", date: "", time: "" });
      setShowPopup(false);
    }
  };

  return (
    <div className="min-h-screen bg-black text-white flex flex-col items-center p-6">
      <h1 className="text-3xl font-bold mb-6">Football Matches</h1>
      <button
        onClick={() => setShowPopup(true)}
        className="bg-white text-black px-4 py-2 rounded-lg font-semibold mb-4"
      >
        Add Match
      </button>

      <div className="grid gap-4 w-full max-w-2xl">
        {matches.map((match, index) => (
          <div key={index} className="border border-white p-4 rounded-lg shadow-md">
            <p className="text-xl font-semibold">{match.team1} vs {match.team2}</p>
            <p className="text-gray-400">{match.date} at {match.time}</p>
          </div>
        ))}
      </div>

      {showPopup && (
        <div className="fixed inset-0 bg-black bg-opacity-80 flex items-center justify-center">
          <div className="bg-white text-black p-6 rounded-lg w-80">
            <h2 className="text-xl font-bold mb-4">Add New Match</h2>
            <input
              type="text"
              placeholder="Team 1"
              value={form.team1}
              onChange={(e) => setForm({ ...form, team1: e.target.value })}
              className="w-full p-2 border rounded mb-2"
            />
            <input
              type="text"
              placeholder="Team 2"
              value={form.team2}
              onChange={(e) => setForm({ ...form, team2: e.target.value })}
              className="w-full p-2 border rounded mb-2"
            />
            <input
              type="date"
              value={form.date}
              onChange={(e) => setForm({ ...form, date: e.target.value })}
              className="w-full p-2 border rounded mb-2"
            />
            <input
              type="time"
              value={form.time}
              onChange={(e) => setForm({ ...form, time: e.target.value })}
              className="w-full p-2 border rounded mb-2"
            />
            <div className="flex justify-between mt-4">
              <button
                onClick={() => setShowPopup(false)}
                className="px-4 py-2 bg-gray-300 rounded-lg"
              >
                Cancel
              </button>
              <button
                onClick={addMatch}
                className="px-4 py-2 bg-black text-white rounded-lg"
              >
                Add
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
