import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

export default function TeamsPage({ isAdmin }) {
    const navigate = useNavigate();
    const [isAddingTeam, setIsAddingTeam] = useState(false); // State to toggle team creation form
    const [teamName, setTeamName] = useState(""); // State for team name
    const [selectedPlayers, setSelectedPlayers] = useState([]); // State for selected players
    const [players, setPlayers] = useState([]); // State for the list of unregistered players
    const [searchQuery, setSearchQuery] = useState(""); // State for search query
    const [filteredPlayers, setFilteredPlayers] = useState([]); // State for filtered players based on search

    // Simulate fetching players from the backend (replace with actual API call)
    useEffect(() => {
        // Example JSON data from the backend
        const fetchPlayers = async () => {
            const response = await fetch("/api/unregistered-players"); // Replace with your API endpoint
            const data = await response.json();
            setPlayers(data);
            setFilteredPlayers(data); // Initialize filtered players with all players
        };
        fetchPlayers();
    }, []);

    // Navigation handlers
    function handleClickTeams() {
        navigate('/teams');
    }

    function handleClickMatches() {
        navigate('/matches');
    }

    function handleClickTables() {
        navigate('/tables');
    }

    function handleLogout() {
        sessionStorage.removeItem("accessToken");
        sessionStorage.removeItem("refreshToken");
        localStorage.removeItem("isAdmin");
        navigate('/login');
    }

    // Toggle team creation form
    function handleAddTeamClick() {
        setIsAddingTeam(!isAddingTeam);
    }

    // Handle team name input change
    function handleTeamNameChange(e) {
        setTeamName(e.target.value);
    }

    // Handle search input change
    function handleSearchChange(e) {
        const query = e.target.value;
        setSearchQuery(query);
        const filtered = players.filter(player =>
            player.toLowerCase().includes(query.toLowerCase())
        );
        setFilteredPlayers(filtered);
    }

    // Handle player selection
    function handlePlayerSelect(player) {
        if (selectedPlayers.includes(player)) {
            // If player is already selected, remove them
            setSelectedPlayers(selectedPlayers.filter(p => p !== player));
        } else {
            // If player is not selected, add them (up to 7 players)
            if (selectedPlayers.length < 7) {
                setSelectedPlayers([...selectedPlayers, player]);
            } else {
                alert("You can only select 7 players.");
            }
        }
    }

    // Handle form submission
    function handleSubmit(e) {
        e.preventDefault();
        if (teamName.trim() === "") {
            alert("Please enter a team name.");
            return;
        }
        if (selectedPlayers.length !== 7) {
            alert("Please select exactly 7 players.");
            return;
        }

        console.log("Team Name:", teamName);
        console.log("Selected Players:", selectedPlayers);

        // Reset form
        setTeamName("");
        setSelectedPlayers([]);
        setIsAddingTeam(false);
    }

    return (
        <>
            <div className="content-teams">
                <div className="container-teams">
                    <header className='header'>
                        <h2 className='logo'>Sportify</h2>
                        <div className='nav'>
                            <ul>
                                <li onClick={handleClickTeams}>Teams</li>
                                <li onClick={handleClickMatches}>Matches</li>
                                <li onClick={handleClickTables}>Tables</li>
                            </ul>
                        </div>
                        {isAdmin && (
                            <button className="button-4" role="button" onClick={handleLogout}>Logout</button>
                        )}
                    </header>

                    {isAdmin && (
                        <button className="button-4" onClick={handleAddTeamClick}>
                            {isAddingTeam ? "Cancel" : "Add Team"}
                        </button>
                    )}

                    {isAddingTeam && (
                        <form onSubmit={handleSubmit} className="team-form">
                            <div>
                                <label htmlFor="teamName">Team Name:</label>
                                <input
                                    type="text"
                                    id="teamName"
                                    value={teamName}
                                    onChange={handleTeamNameChange}
                                    placeholder="Enter team name"
                                    required
                                />
                            </div>
                            <div>
                                <label>Search and Select 7 Players:</label>
                                <input
                                    type="text"
                                    placeholder="Search players..."
                                    value={searchQuery}
                                    onChange={handleSearchChange}
                                />
                                <div className="player-list">
                                    {filteredPlayers.map((player, index) => (
                                        <div
                                            key={index}
                                            className={`player-item ${selectedPlayers.includes(player) ? "selected" : ""}`}
                                            onClick={() => handlePlayerSelect(player)}
                                        >
                                            {player}
                                        </div>
                                    ))}
                                </div>
                            </div>
                            <button type="submit" className="button-4">Create Team</button>
                        </form>
                    )}
                </div>
            </div>
        </>
    );
}
