// src/App.js
import React, { useState } from 'react';
import './matches.css';

function Match({ team1, team2, date, time }) {
  return (
    <div className="match">

      <p>Date: {date}</p>
      <div className='teams'>
      <h3>{team1}  {team2}</h3>vs
      </div>
      <p>Time: {time}</p>
    </div>
  );
};

const MatchesList = ({ matches }) => {
  return (
    <div className="matches-list">
      {matches.map((match, index) => (
        <Match
          key={index}
          team1={match.team1}
          team2={match.team2}
          date={match.date}
          time={match.time}
        />
      ))}
    </div>
  );
};

const Matches = () => {
  const [matches, setMatches] = useState([]);

  // State to manage form inputs
  const [newMatch, setNewMatch] = useState({
    team1: '',
    team2: '',
    date: '',
    time: '',
  });

  // Handle input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewMatch({
      ...newMatch,
      [name]: value,
    });
  };

  // Handle form submission
  const handleAddMatch = (e) => {
    e.preventDefault();
    if (newMatch.team1 && newMatch.team2 && newMatch.date && newMatch.time) {
      setMatches([...matches, newMatch]); 
      setNewMatch({ team1: '', team2: '', date: '', time: '' });
    } else {
      alert('Please fill in all fields!');
    }
  };

  return (
    <div className="App">
      <h1>Upcoming Matches</h1>
      <MatchesList matches={matches} />

      {/* Form to add a new match */}
      <form onSubmit={handleAddMatch} className="add-match-form">
        <h2>Add a New Match</h2>
        <div className="form-group">
          <label>Team 1:</label>
          <input
            type="text"
            name="team1"
            value={newMatch.team1}
            onChange={handleInputChange}
            placeholder="Enter Team 1"
            required
          />
        </div>
        <div className="form-group">
          <label>Team 2:</label>
          <input
            type="text"
            name="team2"
            value={newMatch.team2}
            onChange={handleInputChange}
            placeholder="Enter Team 2"
            required
          />
        </div>
        <div className="form-group">
          <label>Date:</label>
          <input
            type="date"
            name="date"
            value={newMatch.date}
            onChange={handleInputChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Time:</label>
          <input
            type="time"
            name="time"
            value={newMatch.time}
            onChange={handleInputChange}
            required
          />
        </div>
        <button type="submit" className="add-match-button">
          Add Match
        </button>
      </form>
    </div>
  );
};

export default Matches;
