import { createContext, useContext, useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);
  const [isAuth, setIsAuth] = false;
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    checkAuth();
  }, []);

  const loginfn = async (login, password) => {
    try {
      await axios.post("http://localhost:8000/api/auth/token", { login, password }, { withCredentials: true });
      setIsAuth(true);
      await checkAuth();
    } catch (error) {
      console.error("Login failed", error);
      setIsAuth(false);
      throw error; // Ensure errors bubble up
    }
  };

  const logout = async () => {
    try {
      await axios.post("http://localhost:8000/api/auth/logout", {}, { withCredentials: true });
      setUser(null);
      setIsAuth(false);
      setIsAdmin(false);
      navigate("/");
    } catch (error) {
      console.error("Logout failed", error);
    }
  };

  const checkAuth = async () => {
    try {
      const response = await axios.get("http://localhost:8000/api/users/me", { withCredentials: true });
      setUser(response.data.user);
      setIsAdmin(response.data.user.is_admin === true);
      setIsAuth(true);
    } catch (error) {
      setUser(null);
      setIsAuth(false);
      setIsAdmin(false);
    } finally {
      setLoading(false);
    }
  };

  return (
    <AuthContext.Provider value={{ user, isAdmin, isAuth, loginfn, logout, loading }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
