const Matches = () => {
  const [matches, setMatches] = useState([]);
  const [isFormVisible, setIsFormVisible] = useState(false);

  const [newMatch, setNewMatch] = useState({
    team1: '',
    team2: '',
    date: '',
    time: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewMatch({
      ...newMatch,
      [name]: value,
    });
  };

  const handleAddMatch = (e) => {
    e.preventDefault();
    if (newMatch.team1 && newMatch.team2 && newMatch.date && newMatch.time) {
      setMatches([...matches, newMatch]);
      setNewMatch({ team1: '', team2: '', date: '', time: '' });
      setIsFormVisible(false); // Hide the form after adding a match
    } else {
      alert('Please fill in all fields!');
    }
  };

  return (
    <div className="App">
      <video autoPlay muted loop id="myVideo-matches">
        <source src={videoSource} type="video/mp4" />
      </video>
      <h1>Upcoming Matches</h1>

      {/* Button to toggle form visibility */}
      <button
        className="add-match-button"
        onClick={() => setIsFormVisible(!isFormVisible)}
      >
        {isFormVisible ? 'Cancel' : 'Add Match'}
      </button>

      {/* Form to add a new match */}
      {isFormVisible && (
        <div className="form-overlay">
          <form onSubmit={handleAddMatch} className="add-match-form">
            <h2>Add a New Match</h2>
            <div className="form-group">
              <label>Team 1:</label>
              <input
                type="text"
                name="team1"
                value={newMatch.team1}
                onChange={handleInputChange}
                placeholder="Enter Team 1"
                required
              />
            </div>
            <div className="form-group">
              <label>Team 2:</label>
              <input
                type="text"
                name="team2"
                value={newMatch.team2}
                onChange={handleInputChange}
                placeholder="Enter Team 2"
                required
              />
            </div>
            <div className="form-group">
              <label>Date:</label>
              <input
                type="date"
                name="date"
                value={newMatch.date}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="form-group">
              <label>Time:</label>
              <input
                type="time"
                name="time"
                value={newMatch.time}
                onChange={handleInputChange}
                required
              />
            </div>
            <button type="submit" className="add-match-button">
              Add Match
            </button>
          </form>
        </div>
      )}
      <MatchesList matches={matches} />
    </div>
  );
};

export default Matches;
