import React from 'react';
import { useState } from 'react'
import './register.css';
import videoSource from '../assets/background-video.mp4'
import { useNavigate } from 'react-router-dom';

function RegisterPage({setStatus}) {
  const [login, getUsername] = useState("");
  const [password, getPassword] = useState("");
  const [passwordC, getPasswordC] = useState("");

  const navigate = useNavigate();
  function Handleregister(e) {
    e.preventDefault();


    // if (username === "admin" && password === "admin") {
    //   setStatus(true);
      // navigate('/');
    // } else {
    //   alert("Failed");
    // }
    fetch('http://127.0.0.1:8000/api/token', {
      method: "POST",
      body: JSON.stringify({
        login: login,
        password: password,
        passwordC: password,
      }),
      headers: {
        "Content-type": "application/json",
        "Accept": "application/json",
      }
    })
      .then(response => {
        return response.json()
      })
      .then(data => {
        if (data && data.access) {  
        navigate('/');
        setStatus(true)
        sessionStorage.setItem("accessToken", data.access);  
        sessionStorage.setItem("refreshToken", data.refresh);  
        console.log("Tokens stored in sessionStorage!");
    }})
      .catch(error => {
        //handle error
      });
  }


  return (
    <>
    <div className='content-register'>
    <div className='firstlayer-one'></div>
     <video autoPlay muted loop id="myVideo-register">
        <source src={videoSource} type="video/mp4" />
    </video>
    <div className='register-page'>
    <div className="register-container">
      <form className="rgi-form">
        <h1 className="register-title">register</h1>

        <label htmlFor="username" className="register-label">Username</label>
        <input type="text" value={login} onChange={(e) => getUsername(e.target.value)} id="username" placeholder="Enter your username" className="register-input" required />

        <label htmlFor="password" className="register-label">Password</label>
        <input type="password" value={password} onChange={(e) => getPassword(e.target.value)} id="password" placeholder="Enter your password" className="register-input" required />
        <label htmlFor="password" className="register-label">Confirm Your Password</label>
        <input type="password" value={password} onChange={(e) => getPassword(e.target.value)} id="password" placeholder="Confirm Your Password" className="register-input" required />
        <button type="submit" onClick={Handleregister}  className="register-button">Register</button>
      </form>
    </div>
    </div>
    </div>
    </>
  );
}

export default RegisterPage;
