import React from 'react';
import { useState } from 'react'
import './login.css';
import videoSource from '../assets/background-video.mp4'
import { useNavigate } from 'react-router-dom';

function LoginPage(props) {
  const [username, getUsername] = useState("");
  const [password, getPassword] = useState("");

  const navigate = useNavigate();
  function HandleLogin(e) {
    e.preventDefault();

    if (username === "admin" && password === "admin") {
      props.setStatus(true); // Update status in the parent component
      navigate('/'); // Navigate to the landing page
    } else {
      alert("Failed"); // Show error message for invalid credentials
    }
  }


  return (
    <>
    <div className='firstlayer-one'></div>
     <video autoPlay muted loop id="myVideo">
        <source src={videoSource} type="video/mp4" />
    </video>
    <div className='login-page'>
    <div className="login-container">
      <form className="login-form">
        <h1 className="login-title">Login</h1>

        <label htmlFor="username" className="login-label">Username</label>
        <input type="text" value={username} onChange={(e) => getUsername(e.target.value)} id="username" placeholder="Enter your username" className="login-input" required />

        <label htmlFor="password" className="login-label">Password</label>
        <input type="password" value={password} onChange={(e) => getPassword(e.target.value)} id="password" placeholder="Enter your password" className="login-input" required />

        <button type="submit" onClick={HandleLogin}  className="login-button">Log In</button>
      </form>
    </div>
    </div>
    </>
  );
}

export default LoginPage;
