import React, { useState } from 'react';
import './matches.css';

function Match({ team1, team2, date, time }) {
  return (
    <div className="match">
      <p>Date: {date}</p>
      <div className='teams'>
        <h3>{team1}</h3>
        <h2>vs</h2>
        <h3>{team2}</h3>
      </div>
      <p>Time: {time}</p>
    </div>
  );
}

const MatchesList = ({ matches }) => {
  return (
    <div className="matches-list">
      {matches.map((match, index) => (
        <Match
          key={index}
          team1={match.team1}
          team2={match.team2}
          date={match.date}
          time={match.time}
        />
      ))}
    </div>
  );
};

const Matches = () => {
  const [matches, setMatches] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false); // State to control modal visibility

  const [newMatch, setNewMatch] = useState({
    team1: '',
    team2: '',
    date: '',
    time: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewMatch({
      ...newMatch,
      [name]: value,
    });
  };

  const handleAddMatch = (e) => {
    e.preventDefault();
    if (newMatch.team1 && newMatch.team2 && newMatch.date && newMatch.time) {
      setMatches([...matches, newMatch]);
      setNewMatch({ team1: '', team2: '', date: '', time: '' });
      setIsModalOpen(false); // Close the modal after adding a match
    } else {
      alert('Please fill in all fields!');
    }
  };

  return (
    <div className="App">
      <h1>Upcoming Matches</h1>

      <MatchesList matches={matches} />
      <button id="open-modal-button" onClick={() => setIsModalOpen(true)}>
        Add Match
      </button>

      {/* Modal Overlay */}
      {isModalOpen && (
        <div id="modal-overlay" onClick={() => setIsModalOpen(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <h2>Add a New Match</h2>
            <form onSubmit={handleAddMatch} className="add-match-form">
              <div className="form-group">
                <label>Team 1:</label>
                <input
                  type="text"
                  name="team1"
                  value={newMatch.team1}
                  onChange={handleInputChange}
                  placeholder="Enter Team 1"
                  required
                />
              </div>
              <div className="form-group">
                <label>Team 2:</label>
                <input
                  type="text"
                  name="team2"
                  value={newMatch.team2}
                  onChange={handleInputChange}
                  placeholder="Enter Team 2"
                  required
                />
              </div>
              <div className="form-group">
                <label>Date:</label>
                <input
                  type="date"
                  name="date"
                  value={newMatch.date}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Time:</label>
                <input
                  type="time"
                  name="time"
                  value={newMatch.time}
                  onChange={handleInputChange}
                  required
                />
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Matches;
