import { useState, useEffect } from "react";
import LoginPage from "./login/login";
import RegisterPage from "./register.js/register";
import Matches from "./matches/matches";
import TeamsPage from "./teams/teams";
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import LandingPage from "./landingpage/landPage";

function App() {
  // Initialize isAdmin by parsing the value from local storage
  const [isAdmin, setAdmin] = useState(() => {
    const isAdminLocalVariable = localStorage.getItem("isAdmin");
    return isAdminLocalVariable === "true"; // Convert string to boolean
  });

  // Update isAdmin in both state and local storage
  function setStatus(status) {
    localStorage.setItem("isAdmin", status); // Store as string
    setAdmin(status); // Update state
  }

  // Handle logout by resetting isAdmin
  function handleLogout() {
    localStorage.removeItem("isAdmin"); // Remove isAdmin from local storage
    setAdmin(false); // Reset isAdmin state
  }

  return (
    <Router>
      <Routes>
        <Route path="/" element={<LandingPage isAdmin={isAdmin} />} />
        <Route path="/login" element={<LoginPage setStatus={setStatus} isAdmin={isAdmin} />} />
        <Route path="/register" element={<RegisterPage setStatus={setStatus} isAdmin={isAdmin} />} />
        <Route path="/matches" element={<Matches setStatus={setStatus} isAdmin={isAdmin} />} />
        <Route
          path="/teams"
          element={<TeamsPage setStatus={setStatus} isAdmin={isAdmin} onLogout={handleLogout} />}
        />
      </Routes>
    </Router>
  );
}

export default App;
