import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import './teams.css';

function Team({ team1, matches, wins, loses, draws }) {
    // Team component implementation (if needed)
}

export default function TeamsPage({ isAdmin }) {
    const navigate = useNavigate();
    const [teamName, setTeamName] = useState("");
    const [players, setPlayers] = useState(["", "", "", "", "", "", ""]);
    const [teams, setTeams] = useState([]);

    function handleClickTeams() {
        navigate('/teams');
    }

    function handleClickMatches() {
        navigate('/matches');
    }

    function handleClickTables() {
        navigate('/tables');
    }

    function handleLogout() {
        sessionStorage.removeItem("accessToken");
        sessionStorage.removeItem("refreshToken");
        localStorage.removeItem("isAdmin");
        navigate('/login');
    }

    const handlePlayerChange = (index, value) => {
        const newPlayers = [...players];
        newPlayers[index] = value;
        setPlayers(newPlayers);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const newTeam = {
            name: teamName,
            players: players.filter(player => player.trim() !== "")
        };
        setTeams([...teams, newTeam]);
        setTeamName("");
        setPlayers(["", "", "", "", "", "", ""]);
    };

    return (
        <>
            <div className="content-teams">
                <div className="container-teams">
                    <header className='header'>
                        <h2 className='logo'>Sportify</h2>
                        <div className='nav'>
                            <ul>
                                <li onClick={handleClickTeams}>Teams</li>
                                <li onClick={handleClickMatches}>Matches</li>
                                <li onClick={handleClickTables}>Tables</li>
                            </ul>
                        </div>
                        {isAdmin && (
                            <button className="button-4" role="button" onClick={handleLogout}>Logout</button>
                        )}
                    </header>
                    <div className="form">
                        <div className="title">Hi!</div>
                        <div className="subtitle">Let's create a team</div>

                        <form onSubmit={handleSubmit}>
                            <div className="input-container ic1">
                                <input
                                    id="firstname"
                                    className="input"
                                    type="text"
                                    placeholder="Name of the Team"
                                    value={teamName}
                                    onChange={(e) => setTeamName(e.target.value)}
                                />
                            </div>
                            <div className="input-row">
                                {players.map((player, index) => (
                                    <div className="input-container ic2" key={index}>
                                        <input
                                            id={`input${index + 1}`}
                                            className="input"
                                            type="text"
                                            placeholder={`Player ${index + 1}`}
                                            value={player}
                                            onChange={(e) => handlePlayerChange(index, e.target.value)}
                                        />
                                    </div>
                                ))}
                            </div>
                            <button type="submit" className="submit">Submit</button>
                        </form>
                    </div>

                </div>
            </div>
        </>
    );
}
