import React, { useState } from 'react';
import './matches.css';
import videoSource from '../assets/video-back-2.mp4';

function Match({ team1, team2, date, time }) {
  return (
<div className="matches-container">
  <div className="match">
    <div className="teams">
      <h3>{team1}</h3>
      <div className="vs-container">
        <p className="date-time">Date: {date}</p>
        <h2>vs</h2>
        <p className="date-time">Time: {time}</p>
      </div>
      <h3>{team2}</h3>
    </div>
  </div>
  <div className="match">
    <div className="teams">
      <h3>{team3}</h3>
      <div className="vs-container">
        <p className="date-time">Date: {date}</p>
        <h2>vs</h2>
        <p className="date-time">Time: {time}</p>
      </div>
      <h3>{team4}</h3>
    </div>
  </div>
</div>
  );
}

const MatchesList = ({ matches }) => {
  return (
    <div className="matches-list">
      {matches.map((match, index) => (
        <Match
          key={index}
          team1={match.team1}
          team2={match.team2}
          date={match.date}
          time={match.time}
        />
      ))}
    </div>
  );
};

const Matches = () => {
  const [matches, setMatches] = useState([]);
  const [isFormVisible, setIsFormVisible] = useState(false);

  const [newMatch, setNewMatch] = useState({
    team1: '',
    team2: '',
    date: '',
    time: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewMatch({
      ...newMatch,
      [name]: value,
    });
  };

  const handleAddMatch = (e) => {
    e.preventDefault();
    if (newMatch.team1 && newMatch.team2 && newMatch.date && newMatch.time) {
      setMatches([...matches, newMatch]);
      setNewMatch({ team1: '', team2: '', date: '', time: '' });
      setIsFormVisible(false);
    } else {
      alert('Please fill in all fields!');
    }
  };

  return (
    <div className="App">
      <div className="video-container">
        <video autoPlay muted loop id="myVideo-matches">
          <source src={videoSource} type="video/mp4" />
        </video>
        <div className="video-overlay"></div>
      </div>
      <h1>Upcoming Matches</h1>
      <button
        className="add-match-button"
        onClick={() => setIsFormVisible(!isFormVisible)}
      >
        Add Match
      </button>


      {isFormVisible && (
        <div className="form-match-overlay">
          <form onSubmit={handleAddMatch} className="add-match-form">
            <h2>Add a New Match</h2>
            <div className="form-group">
              <label>Team 1:</label>
              <input
                type="text"
                name="team1"
                value={newMatch.team1}
                onChange={handleInputChange}
                placeholder="Enter Team 1"
                required
              />
            </div>
            <div className="form-group">
              <label>Team 2:</label>
              <input
                type="text"
                name="team2"
                value={newMatch.team2}
                onChange={handleInputChange}
                placeholder="Enter Team 2"
                required
              />
            </div>
            <div className="form-group">
              <label>Date:</label>
              <input
                type="date"
                name="date"
                value={newMatch.date}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="form-group">
              <label>Time:</label>
              <input
                type="time"
                name="time"
                value={newMatch.time}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="form-buttons">
              <button type="submit" className="add-match-button">
                Add Match
              </button>
              <button
                type="button"
                className="cancel-button"
                onClick={() => setIsFormVisible(false)}
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}
      <MatchesList matches={matches} />
    </div>
  );
};

export default Matches;
